package reus_act4;

public class ej1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1 = 3, n2 = 3;
		int suma = n1 + n2;
		int resta = n1 - n2;
		int multiplicar = n1 * n2;
		int dividir = n1 / n2;
		int modulo = n1 % n2;
		
		System.out.println("suma:" + suma + "\nresta: "+
							resta + "\nmultiplicacion: "+
							multiplicar + "\ndivision: "+
							dividir + "\nmodulo: "+ modulo);
							
	}

}
