package reus_act4;

public class ej3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int X = 5, Y = 5;
		double N = 5.5, M = 6.5;
		
		System.out.println("N:"+ N + "\n M:"+ M + "\n X:"+ X + "\n Y:"+ Y+"\n");
		
		System.out.println("suma: "+ (X+Y));
		System.out.println("resta: "+ (X-Y));
		System.out.println("producto: "+ (X*Y));
		System.out.println("cocionte: "+ (X/Y));
		System.out.println("resto: "+ (X%Y)+"\n");
		
		System.out.println("suma: "+ (N+M));
		System.out.println("resta: "+ (N-M));
		System.out.println("producto: "+ (N*M));
		System.out.println("cocionte: "+ (N/M));
		System.out.println("resto: "+ (N%M)+"\n");
		
		System.out.println("doble X: "+ (X*2)+
							"\ndoble Y: "+ (Y*2)+
							"\ndoble N: "+ (N*2)+
							"\ndoble M: "+ (M*2)+"\n");
		
		System.out.println("suma de todos: "+ (Y+X+N+M)+"\n");
		
		System.out.println("producto de todos: "+ (Y*X*N*M)+"\n");
	
	}

}
